
(function(e){
  "use strict";

  e("header .more-button").click(function(event) {
    e(".modals .modal-more").modal("show");
  });

  e("#try button").click(function(event) {
    e(".modals .modal-try").modal("show");
  });

  e(".card-body .buy").click(function(event) {
    e(".modals .modal-more .product input").val(e(this).parent().find(".card-title").text());
    e(".modals .modal-buy").modal("show");
  });

  e('.multiple-items').slick({
    infinite: true,
    prevArrow:'<button class="slick-prev"></button>',
    nextArrow:'<button class="slick-next"></button>',
    dots: false,
    responsive: [
      {
        breakpoint: 1920,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 1366,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
      // You can unslick at a given breakpoint now by adding:
      // settings: "unslick"
      // instead of a settings object
    ]
  });
})(jQuery);
